import React, {Component} from 'react'
import './Sidebar.css'
import arrow from './Photos/arrow.png'
import menu from './Photos/menu.png'
import egg from './Photos/egg.png'
import cake from './Photos/dessert.png'
import coffee from './Photos/coffee.png'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useParams,
    useRouteMatch
  } from "react-router-dom";

class Sidebar extends Component{
    constructor(props){
        super(props);
        this.state = {value:false};
    }
    changeState(){
        this.setState({value: !this.state.value});
    }


    render(){
        const x = 300;
        const y = 0;
        const x1 = 200;
        const open = { 
        transform: `translate(${x}px, ${y}px)`,
        transition: 'all 0.5s'
        };
        const close = {
            transform: `translate(-0px, ${y}px)`,
            transition: 'all 0.5s',
        };

        let btn_class = this.state.value ? open: close;


        return(

            <div className="sidebar" style={btn_class}>

                <div className="hidden">
                    <p>MENU</p>
                    <Link to="/"><p className="navMenu">Home</p></Link>
                    <img src={egg}/>
                    <Link to="/Brunch"></Link><p className="navMenu">Brunch</p>
                    <img src={coffee}/>
                    <p className="navMenu">Coffee Shops</p>
                    <img src={cake}/>
                    <p className="navMenu">Dessert</p>
                    <p>Cocktails</p>
                </div>
                <img className="Back" src={arrow}/>
                <p className="name">karen's food thoughts</p>
                <div class="menu-container">
                    <div class="bar1"></div>
                    <div class="bar2"></div>
                    <div class="bar3"></div>
                </div>
                <img className="Menu" src={menu} onClick={this.changeState.bind(this)}/>
            </div>
        )
    }
}

export default Sidebar
